package basicJava;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20,temp;
		System.out.println("Before swapping the value of a is "+a+" and b is "+b);
		temp = a;
		a = b ;
		b = temp;
		System.out.println("\nAfter swapping the value of a is "+a+" and b is "+b);

	}

}
